import { Component } from '@angular/core';
import { Client } from '../models/client';
import { ClientService } from '../service/client.service';

@Component({
  selector: 'app-signup-form',
  templateUrl: './signup-form.component.html',
  styleUrls: ['./signup-form.component.css']
})

export class SignupFormComponent {
  // Define public properties to store form input values
  public name: string = '';
  public cif: string = '';
  public address: string = '';
  public group: number = 0;

  // Inject the ClientService into the component's constructor
  constructor(private clientService: ClientService) { }

  // Define an onSubmit method to handle form submissions
  onSubmit() {
    // Validate form input values
    if (this.name == '') {
      window.alert("Name cannot be empty!");
    } else if (this.cif == '') {
      window.alert("CIF cannot be empty!");
    } else if (this.address == '') {
      window.alert("Address cannot be empty!");
    } else if (this.group <= 0) {
      window.alert("Group cannot be 0 or negative!");
    } else {
      // Create a new Client object with the input values
      const newClient = new Client(this.name, this.cif, this.address, this.group);

      // Add the new client to the clientService
      this.clientService.addClient(newClient);

      // Reset form input values
      this.name = '';
      this.cif = '';
      this.address = '';
      this.group = 0;
    }
  }

}
<div class="container mb-3">
  <h2 class="mb-3">Client Signup</h2>
  <form (ngSubmit)="onSubmit()">
    <div class="d-flex mb-3 justify-content-evenly align-items-center">
      <label for="client-name" class="form-label pe-3">Name:</label>
      <input type="text" [(ngModel)]="name" name="name" class="form-control" id="client-name">
    </div>
    <div class="d-flex mb-3 justify-content-evenly align-items-center">
      <label for="cif-num" class="form-label pe-3">CIF:</label>
      <input type="text" [(ngModel)]="cif" name="cif" class="form-control" id="cif-num">
    </div>
    <div class="d-flex mb-3 justify-content-evenly align-items-center">
      <label for="client-address" class="form-label pe-3">Address:</label>
      <input type="text" [(ngModel)]="address" name="address" class="form-control" id="client-address">
    </div>
    <div class="d-flex mb-3 justify-content-evenly align-items-center">
      <label for="client-group" class="form-label pe-3">Group:</label>
      <input type="number" [(ngModel)]="group" name="group" class="form-control" id="client-group">
    </div>
    <div class="d-flex mb-3 justify-content-center align-items-center">
      <button type="submit" class="btn btn-primary mb-2">Send</button>
    </div>

  </form>
</div>
